INSERT INTO `` (`id`,`title`,`str_ingredient1`,`str_ingredient2`,`str_ingredient3`,`str_ingredient4`,`str_ingredient5`,`str_ingredient6`,`str_ingredient7`,`str_ingredient8`,`str_ingredient9`,`str_ingredient10`,`str_ingredient11`,`str_ingredient12`,`str_ingredient13`,`str_ingredient14`,`str_ingredient15`,`str_ingredient16`,`str_ingredient17`,`str_ingredient18`,`str_ingredient19`,`str_ingredient20`,`instructions`,`created_at`,`updated_at`) VALUES (1,'Moroccan Carrot Soup','6 chopped Carrots','1 sliced Onion','4 Garlic Clove','1 tsp  Cumin','1/2 tsp Coriander','1 tbs Olive Oil','1/4 tsp Garam Masala','1 tsp  Lemon Juice','  ','  ','  ','  ','  ','  ','  ','  ','  ','  ','  ','  ','\"Step 1\\r\\nPreheat oven to 180° C.\\r\\nStep 2\\r\\nCombine carrots, onion, garlic, cumin seeds, coriander seeds, salt and olive oil in a bowl and mix well. Transfer on a baking tray.\\r\\nStep 3\\r\\nPut the baking tray in preheated oven and roast for 10-12 minutes or till carrots soften. Remove from heat and cool.\\r\\nStep 4\\r\\nGrind the baked carrot mixture along with some water to make a smooth paste and strain in a bowl.\\r\\nStep 5\\r\\nHeat the carrot mixture in a non-stick pan. Add two cups of water and bring to a boil. Add garam masala powder and mix. Add salt and mix well.\\r\\nStep 6\\r\\nRemove from heat, add lemon juice and mix well.\\r\\nStep 7\\r\\nServe hot.\"','2022-09-17 20:16:18','2022-09-17 20:16:18');
INSERT INTO `` (`id`,`title`,`str_ingredient1`,`str_ingredient2`,`str_ingredient3`,`str_ingredient4`,`str_ingredient5`,`str_ingredient6`,`str_ingredient7`,`str_ingredient8`,`str_ingredient9`,`str_ingredient10`,`str_ingredient11`,`str_ingredient12`,`str_ingredient13`,`str_ingredient14`,`str_ingredient15`,`str_ingredient16`,`str_ingredient17`,`str_ingredient18`,`str_ingredient19`,`str_ingredient20`,`instructions`,`created_at`,`updated_at`) VALUES (2,'Chicken Congee','8 oz  Chicken','pinch Salt','pinch Pepper','1 tsp  Ginger Cordial','1 tsp  Ginger','1 tbs Spring Onions','1/2 cup  Rice','8 cups  Water','2 oz  Coriander',' ',' ',' ',' ',' ',' ',' ',' ',' ',' ',' ','\"STEP 1 - MARINATING THE CHICKEN\\r\\nIn a bowl, add chicken, salt, white pepper, ginger juice and then mix it together well.\\r\\nSet the chicken aside.\\r\\nSTEP 2 - RINSE THE WHITE RICE\\r\\nRinse the rice in a metal bowl or pot a couple times and then drain the water.\\r\\nSTEP 2 - BOILING THE WHITE RICE\\r\\nNext add 8 cups of water and then set the stove on high heat until it is boiling. Once rice porridge starts to boil, set the stove on low heat and then stir it once every 8-10 minutes for around 20-25 minutes.\\r\\nAfter 25 minutes, this is optional but you can add a little bit more water to make rice porridge to make it less thick or to your preference.\\r\\nNext add the marinated chicken to the rice porridge and leave the stove on low heat for another 10 minutes.\\r\\nAfter an additional 10 minutes add the green onions, sliced ginger, 1 pinch of salt, 1 pinch of white pepper and stir for 10 seconds.\\r\\nServe the rice porridge in a bowl\\r\\nOptional: add Coriander on top of the rice porridge.\"','2022-09-17 20:16:18','2022-09-17 20:16:18');
INSERT INTO `` (`id`,`title`,`str_ingredient1`,`str_ingredient2`,`str_ingredient3`,`str_ingredient4`,`str_ingredient5`,`str_ingredient6`,`str_ingredient7`,`str_ingredient8`,`str_ingredient9`,`str_ingredient10`,`str_ingredient11`,`str_ingredient12`,`str_ingredient13`,`str_ingredient14`,`str_ingredient15`,`str_ingredient16`,`str_ingredient17`,`str_ingredient18`,`str_ingredient19`,`str_ingredient20`,`instructions`,`created_at`,`updated_at`) VALUES (3,'Tahini Lentils','50g Tahini','zest and juice of 1 Lemon','2 tblsp  Olive Oil','1 chopped Red Onions','1 clove peeled crushed Garlic','1 thinly sliced Yellow Pepper','200g Green Beans','1 sliced Courgettes','100g shredded Kale','250g pack Lentils',' ',' ',' ',' ',' ',' ',' ',' ',' ',' ','\"In a jug, mix the tahini with the zest and juice of the lemon and 50ml of cold water to make a runny dressing. Season to taste, then set aside.\\r\\nHeat the oil in a wok or large frying pan over a medium-high heat. Add the red onion, along with a pinch of salt, and fry for 2 mins until starting to soften and colour. Add the garlic, pepper, green beans and courgette and fry for 5 min, stirring frequently.\\r\\nTip in the kale, lentils and the tahini dressing. Keep the pan on the heat for a couple of mins, stirring everything together until the kale is wilted and it’s all coated in the creamy dressing.\"','2022-09-17 20:16:18','2022-09-17 20:16:18');
INSERT INTO `` (`id`,`title`,`str_ingredient1`,`str_ingredient2`,`str_ingredient3`,`str_ingredient4`,`str_ingredient5`,`str_ingredient6`,`str_ingredient7`,`str_ingredient8`,`str_ingredient9`,`str_ingredient10`,`str_ingredient11`,`str_ingredient12`,`str_ingredient13`,`str_ingredient14`,`str_ingredient15`,`str_ingredient16`,`str_ingredient17`,`str_ingredient18`,`str_ingredient19`,`str_ingredient20`,`instructions`,`created_at`,`updated_at`) VALUES (4,'Chicken Marengo','1 tbs Olive Oil','300g Mushrooms','4 Chicken Legs','500g Passata','1 Chicken Stock Cube','100g  Black Olives','Chopped Parsley',' ',' ',' ',' ',' ',' ',' ',' ',' ',' ',' ',' ',' ','\"Heat the oil in a large flameproof casserole dish and stir-fry the mushrooms until they start to soften. Add the chicken legs and cook briefly on each side to colour them a little.\\r\\nPour in the passata, crumble in the stock cube and stir in the olives. Season with black pepper – you shouldn’t need salt. Cover and simmer for 40 mins until the chicken is tender. Sprinkle with parsley and serve with pasta and a salad, or mash and green veg, if you like.\"','2022-09-17 20:16:18','2022-09-17 20:16:18');
INSERT INTO `` (`id`,`title`,`str_ingredient1`,`str_ingredient2`,`str_ingredient3`,`str_ingredient4`,`str_ingredient5`,`str_ingredient6`,`str_ingredient7`,`str_ingredient8`,`str_ingredient9`,`str_ingredient10`,`str_ingredient11`,`str_ingredient12`,`str_ingredient13`,`str_ingredient14`,`str_ingredient15`,`str_ingredient16`,`str_ingredient17`,`str_ingredient18`,`str_ingredient19`,`str_ingredient20`,`instructions`,`created_at`,`updated_at`) VALUES (5,'Shawarma','1 kg Chicken Thighs','1 tbs Coriander','1 tbs Cumin','1 tbs Cardamom','1 tsp  Cayenne Pepper','2 tsp Paprika','2 tbs Lemon Juice','3 tbs Olive Oil','1 cup  Greek Yogurt','1 Garlic Clove','1 tsp  Cumin','Splash Lemon Juice','Sliced Lettuce','Sliced Tomato','6 Pita Bread','  ','  ','  ','  ','  ','\"Combine the marinade ingredients in a large ziplock bag (or bowl).\\r\\nAdd the chicken and use your hands to make sure each piece is coated. If using a ziplock bag, I find it convenient to close the bag then massage the bag to disperse the rub all over each chicken piece.\\r\\nMarinate overnight or up to 24 hours.\\r\\nCombine the Yoghurt Sauce ingredients in a bowl and mix. Cover and put in the fridge until required (it will last for 3 days in the fridge).\\r\\nHeat grill/BBQ (or large heavy based pan on stove) on medium high. You should not need to oil it because the marinade has oil in it and also thigh fillets have fat. But if you are worried then oil your hotplate/grill. (See notes for baking)\\r\\nPlace chicken on the grill and cook the first side for 4 to 5 minutes until nicely charred, then turn and cook the other side for 3 to 4 minutes (the 2nd side takes less time).\\r\\nRemove chicken from the grill and cover loosely with foil. Set aside to rest for 5 minutes.\\r\\nTO SERVE\\r\\nSlice chicken and pile onto platter alongside flatbreads, Salad and the Yoghurt Sauce.\\r\\nTo make a wrap, get a piece of flatbread and smear with Yoghurt Sauce. Top with a bit of lettuce and tomato and Chicken Shawarma. Roll up and enjoy!\"','2022-09-17 20:16:18','2022-09-17 20:16:18');
INSERT INTO `` (`id`,`title`,`str_ingredient1`,`str_ingredient2`,`str_ingredient3`,`str_ingredient4`,`str_ingredient5`,`str_ingredient6`,`str_ingredient7`,`str_ingredient8`,`str_ingredient9`,`str_ingredient10`,`str_ingredient11`,`str_ingredient12`,`str_ingredient13`,`str_ingredient14`,`str_ingredient15`,`str_ingredient16`,`str_ingredient17`,`str_ingredient18`,`str_ingredient19`,`str_ingredient20`,`instructions`,`created_at`,`updated_at`) VALUES (6,'Polskie Naleśniki (Polish Pancakes)','1 cup  Flour','2 Eggs','1 cup  Milk','3/4 cup  Water','Pinch Salt','1 tsp  Sugar','3 tbs Butter','  ','  ','  ','  ','  ','  ','  ','  ','  ','  ','  ','  ','  ','\"Add flour, eggs, milk, water, and salt in a large bowl then mix with a hand mixer until you have a smooth, lump-free batter.\\r\\nAt this point, mix in the butter or the vegetable oil. Alternatively, you can use them to grease the pan before frying each pancake.\\r\\nHeat a non-stick pan over medium heat, then pour in the batter, swirling the pan to help it spread.\\r\\nWhen the pancake starts pulling away a bit from the sides, and the top is no longer wet, flip it and cook shortly on the other side as well.\\r\\nTransfer to a plate. Cook the remaining batter until all used up.\\r\\nServe warm, with the filling of your choice.\"','2022-09-17 20:16:18','2022-09-17 20:16:18');
INSERT INTO `` (`id`,`title`,`str_ingredient1`,`str_ingredient2`,`str_ingredient3`,`str_ingredient4`,`str_ingredient5`,`str_ingredient6`,`str_ingredient7`,`str_ingredient8`,`str_ingredient9`,`str_ingredient10`,`str_ingredient11`,`str_ingredient12`,`str_ingredient13`,`str_ingredient14`,`str_ingredient15`,`str_ingredient16`,`str_ingredient17`,`str_ingredient18`,`str_ingredient19`,`str_ingredient20`,`instructions`,`created_at`,`updated_at`) VALUES (7,'Fresh sardines','500g Sardines','Dash Vegetable Oil','To Glaze Flour','Dash Salt','  ','  ','  ','  ','  ','  ','  ','  ','  ','  ','  ','  ','  ','  ','  ','  ','\"Wash the fish under the cold tap. Roll in the flour and deep fry in oil until crispy. Lay on kitchen towel to get rid of the excess oil and serve hot or cold with a slice of lemon.\"','2022-09-17 20:16:18','2022-09-17 20:16:18');
INSERT INTO `` (`id`,`title`,`str_ingredient1`,`str_ingredient2`,`str_ingredient3`,`str_ingredient4`,`str_ingredient5`,`str_ingredient6`,`str_ingredient7`,`str_ingredient8`,`str_ingredient9`,`str_ingredient10`,`str_ingredient11`,`str_ingredient12`,`str_ingredient13`,`str_ingredient14`,`str_ingredient15`,`str_ingredient16`,`str_ingredient17`,`str_ingredient18`,`str_ingredient19`,`str_ingredient20`,`instructions`,`created_at`,`updated_at`) VALUES (8,'Toad In The Hole','8 sausages','3 tbsp vegetable oil','100g plain flour','2 medium eggs','1 tbsp grated horseradish','225ml milk','200g cherry tomatoes',' ',' ',' ',' ',' ',' ',' ',' ',' ',' ',' ',' ',' ','\"Preheat the oven to 200°C/fan180°C/gas 6. fry sausages in a non-stick pan until browned.\\r\\nDrizzle vegetable oil in a 30cm x 25cm x 6cm deep roasting tray and heat in the oven for 5 minutes.\\r\\nPut the plain flour in a bowl, crack in the medium free-range eggs, then stir in the grated horseradish. Gradually beat in the semi-skimmed milk. Season.\\r\\nPut the sausages into the hot roasting tray and pour over the batter. Top with cherry tomatoes on the vine and cook for 30 minutes until puffed and golden.\"','2022-09-17 20:16:18','2022-09-17 20:16:18');
INSERT INTO `` (`id`,`title`,`str_ingredient1`,`str_ingredient2`,`str_ingredient3`,`str_ingredient4`,`str_ingredient5`,`str_ingredient6`,`str_ingredient7`,`str_ingredient8`,`str_ingredient9`,`str_ingredient10`,`str_ingredient11`,`str_ingredient12`,`str_ingredient13`,`str_ingredient14`,`str_ingredient15`,`str_ingredient16`,`str_ingredient17`,`str_ingredient18`,`str_ingredient19`,`str_ingredient20`,`instructions`,`created_at`,`updated_at`) VALUES (9,'Skillet Apple Pork Chops with Roasted Sweet Potatoes & Zucchini','2 Potatoes','1 Apples','2 cloves Garlic','1 Lemon','2 Pork','1 Zucchini','1 Chicken Stock','1 tbsp Vegetable Oil','1 1/2 tsp  Sugar','2 tbsp Butter','  ','  ','  ','  ','  ','  ','  ','  ','  ','  ','\"\\r\\nServes 2\\r\\n\\r\\n\\r\\n1. \\r\\n\\r\\nAdjust racks to top and middle positions and preheat oven to 450 degrees. Wash and dry all produce. Dice sweet potatoes into 1/2-inch pieces. Toss on a baking sheet with a drizzle of oil, salt, and pepper. Roast on top rack for 12 minutes (we\'ll roast the zucchini then). \\r\\n\\r\\n\\r\\n2. \\r\\n\\r\\nMeanwhile, halve and core apple; thinly slice into half-moons. Peel and finely chop garlic. Quarter lemon. Trim and halve zucchini lengthwise; cut crosswise into 1/2-inch-thick half-moons. Toss on a second baking sheet with a drizzle of oil and a pinch of salt and pepper. Set aside. \\r\\n\\r\\n\\r\\n3. \\r\\n\\r\\nPat pork dry with paper towels and season all over with salt and pepper. Heat a drizzle of oil in a large pan over medium-high heat. Add pork and cook until browned and cooked through, 4-5 minutes per side. Turn off heat; transfer to a plate. \\r\\n\\r\\n\\r\\n4. \\r\\n\\r\\nOnce sweet potatoes have roasted 12 minutes, transfer baking sheet with zucchini to middle rack and continue roasting until both veggies are browned and softened, 12-15 minutes more. \\r\\n\\r\\n\\r\\n5. \\r\\n\\r\\nMeanwhile, melt 1 TBSP butter (2 TBSP for 4 servings) in pan used for pork over medium-high heat. Add apple and season with salt and pepper. Cook, scraping up any browned bits from bottom of pan, until apple is slightly softened, 2-3 minutes. Add garlic; cook until fragrant, 30 seconds. Add 1/z cup water (3/4 cup for 4), stock concentrate, and 11/2 tsp sugar (3 tsp for 4). Cook, stirring','2022-09-17 20:16:18','2022-09-17 20:16:18');
INSERT INTO `` (`id`,`title`,`str_ingredient1`,`str_ingredient2`,`str_ingredient3`,`str_ingredient4`,`str_ingredient5`,`str_ingredient6`,`str_ingredient7`,`str_ingredient8`,`str_ingredient9`,`str_ingredient10`,`str_ingredient11`,`str_ingredient12`,`str_ingredient13`,`str_ingredient14`,`str_ingredient15`,`str_ingredient16`,`str_ingredient17`,`str_ingredient18`,`str_ingredient19`,`str_ingredient20`,`instructions`,`created_at`,`updated_at`) VALUES (10,'Chickpea Fajitas','400g Chickpeas','1 tblsp  Olive Oil','pinch Paprika','2 small cut chunks Tomatoes','1 finely sliced Red Onions','2 tsp Red Wine Vinegar','1 Avocado','Juice of 1 Lime','Chopped Lime','100g  Sour Cream','2 tsp Harissa Spice','4 Corn Tortillas','to serve Coriander',' ',' ',' ',' ',' ',' ',' ','\"Heat oven to 200C/180C fan/gas 6 and line a baking tray with foil. Drain the chickpeas, pat dry and tip onto the prepared baking tray. Add the oil and paprika, toss to coat, then roast for 20-25 mins until browned and crisp, shaking halfway through cooking.\\r\\n\\r\\nMeanwhile, put the tomatoes and onion in a small bowl with the vinegar and set aside to pickle. Put the avocado in another bowl and mash with a fork, leaving some larger chunks. Stir in the lime juice and season well. Mix the soured cream with the harissa and set aside until ready to serve.\\r\\n\\r\\nHeat a griddle pan until nearly smoking. Add the tortillas , one at a time, charring each side until hot with griddle lines. \\r\\n\\r\\nPut everything on the table and build the fajitas : spread a little of the harissa cream over the tortilla, top with roasted chickpeas, guacamole, pickled salsa and coriander, if you like. Serve with the lime wedges for squeezing over.\"','2022-09-17 20:16:18','2022-09-17 20:16:18');
